package topic.string;

import static org.junit.Assert.*;

import java.util.HashMap;
import java.util.Map;

import org.junit.Test;

public class RobotReturntoOrigin_657 {

	public boolean judgeCircle(String moves) {
		Map<Character, Integer> map = new HashMap<Character, Integer>();
		for (char i : moves.toCharArray()) {
			map.put(i, map.getOrDefault(i, 0) + 1);
		}
		return map.getOrDefault('R',0) .equals( map.getOrDefault('L',0) )&& map.getOrDefault('U',0) .equals( map.getOrDefault('D',0));
	}

	@Test
	public void test() throws Exception {
		String str = "";
		boolean judgeCircle = judgeCircle(str);
		assertEquals(true, judgeCircle);
	}
}
